mapper.select:
select eq.id id, eq.code code, eq.title title, c.name courseName, eq.type type
from exam_questions eq
LEFT JOIN course c on eq.course_code = c.code
where 1=1

  and eq.id = #{record.id,jdbcType=INTEGER}


  and eq.code = #{record.code,jdbcType=CHAR}


  and eq.title like concat('%', #{record.title,jdbcType=VARCHAR}, '%')


  and c.code = #{record.courseCode,jdbcType=CHAR}


  and eq.type = #{record.type,jdbcType=CHAR}

select count(1)
from exam_questions eq
LEFT JOIN course c on eq.course_code = c.code
where 1=1

  and eq.id = #{record.id,jdbcType=INTEGER}


  and eq.code = #{record.code,jdbcType=CHAR}


  and eq.title like concat('%', #{record.title,jdbcType=VARCHAR}, '%')


  and c.code = #{record.courseCode,jdbcType=CHAR}


  and eq.type = #{record.type,jdbcType=CHAR}
